import { useState } from 'react'
import { useDispatch } from 'react-redux'
import { createNote } from '../features/notes/notesSlice'

function NoteForm() {

  //constants for title and description

  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')

  const dispatch = useDispatch()

  const onSubmit = (e) => {
    e.preventDefault()

    //dispatch note 
    
    dispatch(createNote({ title, description }))
    setTitle('')
    setDescription('')
  }

  return (
    <section className='form'>
      <form onSubmit={onSubmit}>
        <div className='form-group'>
          <label htmlFor='text' className="text-white">Title</label>
          <input
            type='text'
            name='title'
            id='title'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <label htmlFor='text' className="text-white">Description</label>
          <input
            type='text'
            name='description'
            id='description'
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className='form-group'>
          <button className='btn btn-block' type='submit'>
            Add Note
          </button>
        </div>
      </form>
    </section>
  )
}

export default NoteForm
